mapper.select:
select profile_img, nickname, statement_msg, visible_email, github, website, following_cnt, follower_cnt
from member, 
	(select count(following_id) following_cnt
	from following
	where id=#{id}
	group by id),
	(select count(follower_id) follower_cnt
	from follower
	where id=#{id}
	group by id)
where member.id=#{id}

select count(follower_id) follower_cnt
from follower
where id=#{id}
group by id;
