mapper.select:
select tag_name, count(tag_name) tag_cnt from tag, post, member where post.post_num=tag.post_num and member.id=post.id and member.id=#{id}
and hidden_flag='F'
group by tag_name

select post_num, thumbnail, post_title, post_content, view_cnt, hidden_flag, to_char(input_date, 'yyyy.mm.dd hh24:mi') input_date, nvl(comm_cnt,0) comm_cnt
from (select post.post_num, thumbnail, post_title, post_content, view_cnt, hidden_flag, input_date, comm_cnt, row_number() over(order by input_date desc) rnum
	from post, tag,
		(select post.post_num, count(comm.post_num) comm_cnt
		from post,comm
		where post.post_num=comm.post_num and comm.delete_flag='F' and post.id=#{id}
		group by post.post_num) comm
	where post.post_num=comm.post_num(+) and post.post_num=tag.post_num
	and delete_flag='F'
	
	and hidden_flag='F'
	
	and tag_name=#{column_value}
	and post.id=#{id})
where rnum between #{start_num} and #{end_num}

select tag_name
from tag
where post_num=#{post_num}
