mapper.select:
select post_num, id, nickname, post_title, post_content, thumbnail, to_char(input_date, 'yyyy-mm-dd') input_date, nvl(like_cnt,0) like_cnt
from (
select post.post_num, post.id, nickname, post.post_title, post.post_content, post.thumbnail, post.input_date, LIKE_CNT2.like_cnt, row_number() over(order by LIKE_CNT2.like_cnt desc NULLS LAST, post.input_date desc) rnum
select post.post_num, post.id, nickname, post.post_title, post.post_content, post.thumbnail, post.input_date, LIKE_CNT2.like_cnt, row_number() over(order by post.input_date desc) rnum
from post, member, (select post_num, count(*) as like_cnt from like_post group by post_num) LIKE_CNT2
where post.delete_flag='F' and post.hidden_flag='F' and post.publish_flag='T' and member.id = post.id and post.post_num = LIKE_CNT2.post_num(+)
and (post_title like '%${ column_value }%' or post_content like '%${ column_value }%')
)
where rnum between #{ start_num } and #{ end_num }
