mapper.insert:
	
	into tag(
		post_num, tag_name
	) values
	(
		#{post_num}, #{tag}
	)

select * from dual

mapper.select:
select post_num
from post
where publish_flag = 'F' and id= #{id}

mapper.update:
update post
set	thumbnail = #{thumbnail}, post_title=#{post_title}, post_content = #{post_content}, publish_flag=#{publish_flag}, hidden_flag=#{hidden_flag}
where post_num = #{post_num} and id = #{id}

mapper.select:
select post_num, thumbnail, post_title, post_content, publish_flag, hidden_flag
from post
where post_num = #{post_num}

select tag_name
from tag
where post_num = #{post_num}
